import os
import sys
import traceback


def run(path, num_levels):
	path = os.path.expanduser(path)
	path = os.path.expandvars(path)
	return scan_directory_recursive(path, num_levels)


def scan_directory_recursive(directory, level):
	if level == 0:
		return []

	subfolders = []
	try:
		with os.scandir(directory) as entries:
			for f in entries:
				try:
					if f.is_dir():
						subfolders.append(f.path)
				except (OSError,PermissionError):
					sys.stderr.write(f"Failed access <{f.path}>\n")

		for folder in list(subfolders):
		    subfolders.extend(scan_directory_recursive(folder, level-1))
	except Exception:
		sys.stderr.write(f"Failed scanning directory: <{directory}>, error: {traceback.format_exc()}")

	return subfolders

